
import os
import base64
from tkinter import *
from tkinter import filedialog, messagebox
from tkinter import ttk
from openpyxl import load_workbook
from docx import Document
from datetime import datetime

# base64 템플릿 생략 (조장님이 붙이실 위치)
excel_b64 = """"""
word_b64 = """"""

EXCEL_FILENAME = None

def save_template(b64data, default_name):
    path = filedialog.asksaveasfilename(defaultextension=".xlsx", initialfile=default_name)
    if path:
        with open(path, "wb") as f:
            f.write(base64.b64decode(b64data.encode()))
        messagebox.showinfo("완료", f"템플릿이 저장되었습니다:\n{path}")

def upload_excel():
    global EXCEL_FILENAME
    path = filedialog.askopenfilename(filetypes=[("Excel files", "*.xlsx")])
    if path:
        EXCEL_FILENAME = path
        excel_label.config(text=f"선택한 파일: {os.path.basename(path)}")

def extract_excel_data(filepath):
    wb = load_workbook(filepath)
    ws = wb.active

    data = {
        "부서명": str(ws["B2"].value or ""),
        "요청자": str(ws["F2"].value or ""),
        "일자": str(ws["J2"].value or ""),
        "결재1": str(ws["B4"].value or ""),
        "결재2": str(ws["C4"].value or ""),
        "결재3": str(ws["D4"].value or ""),
        "결재4": str(ws["E4"].value or ""),
        "결재5": str(ws["F4"].value or ""),
        "합계금액": str(ws["J21"].value or ""),
        "비고": str(ws["B22"].value or ""),
        "항목들": []
    }

    for row in ws.iter_rows(min_row=7, max_row=20, min_col=1, max_col=10):
        항목 = {
            "순번": row[0].value,
            "물품명": row[1].value,
            "규격": row[2].value,
            "수량": row[3].value,
            "단가": row[4].value,
            "금액": row[5].value,
            "구매처": row[6].value,
            "비고": row[7].value
        }
        if any(항목.values()):
            data["항목들"].append(항목)

    return data

def fill_word_template(template_path, save_path, data):
    doc = Document(template_path)

    for p in doc.paragraphs:
        for key, value in data.items():
            if isinstance(value, str) and f"{{{key}}}" in p.text:
                p.text = p.text.replace(f"{{{key}}}", value)

    for table in doc.tables:
        for row in table.rows:
            for cell in row.cells:
                for key, value in data.items():
                    if isinstance(value, str) and f"{{{key}}}" in cell.text:
                        cell.text = cell.text.replace(f"{{{key}}}", value)

        # 항목 삽입 위치 찾기
        if "{항목테이블}" in table.rows[1].cells[0].text:
            table._tbl.remove(table.rows[1]._tr)  # 제거
            for 항목 in data["항목들"]:
                row_cells = table.add_row().cells
                row_cells[0].text = str(항목["순번"] or "")
                row_cells[1].text = 항목["물품명"] or ""
                row_cells[2].text = 항목["규격"] or ""
                row_cells[3].text = str(항목["수량"] or "")
                row_cells[4].text = str(항목["단가"] or "")
                row_cells[5].text = str(항목["금액"] or "")
                row_cells[6].text = 항목["구매처"] or ""
                row_cells[7].text = 항목["비고"] or ""

    doc.save(save_path)
    return True

def create_document():
    if not doc_type_var.get():
        messagebox.showwarning("선택 오류", "문서 종류를 선택해주세요.")
        return
    if not format_var.get():
        messagebox.showwarning("선택 오류", "파일 형식을 선택해주세요.")
        return
    if not EXCEL_FILENAME:
        messagebox.showwarning("파일 오류", "입력용 엑셀 파일을 업로드해주세요.")
        return

    path = filedialog.asksaveasfilename(defaultextension=".docx", filetypes=[("Word 문서", "*.docx")])
    if not path:
        return

    try:
        with open("__temp.docx", "wb") as f:
            f.write(base64.b64decode(word_b64.encode()))

        excel_data = extract_excel_data(EXCEL_FILENAME)
        fill_word_template("__temp.docx", path, excel_data)
        os.remove("__temp.docx")

        messagebox.showinfo("완료", f"문서가 저장되었습니다:\n{path}")
    except Exception as e:
        messagebox.showerror("오류 발생", str(e))

# ---------------- UI ------------------
root = Tk()
root.title("사무자동화 문서 생성기")
root.geometry("640x640")
root.configure(padx=20, pady=20)
ttk.Style().theme_use("default")

Label(root, text="사무자동화 문서 생성기", font=("맑은 고딕", 22, "bold")).pack(pady=15)

Button(root, text="물품구매요청서 엑셀 템플릿 다운로드", font=("맑은 고딕", 12),
       command=lambda: save_template(excel_b64, "물품구매요청_입력템플릿.xlsx"), width=42).pack(pady=5)

Button(root, text="대외공문 엑셀 템플릿 다운로드 (준비중)", font=("맑은 고딕", 12), state=DISABLED, width=42).pack(pady=5)

Button(root, text="입력용 엑셀 파일 업로드", font=("맑은 고딕", 12),
       command=upload_excel, width=42, bg="#f0f0f0").pack(pady=8)

excel_label = Label(root, text="선택한 파일: 없음", font=("맑은 고딕", 11), fg="gray")
excel_label.pack(pady=2)

doc_type_var = StringVar(value="")
Label(root, text="문서 종류 선택:", font=("맑은 고딕", 13, "bold")).pack(pady=(15, 3))
ttk.Radiobutton(root, text="물품구매요청서", variable=doc_type_var, value="물품구매요청서").pack(anchor="w", padx=180)
ttk.Radiobutton(root, text="대외공문", variable=doc_type_var, value="대외공문").pack(anchor="w", padx=180)

format_var = StringVar(value="docx")
Label(root, text="파일 형식 선택:", font=("맑은 고딕", 13, "bold")).pack(pady=(15, 3))
ttk.Radiobutton(root, text="워드 (.docx)", variable=format_var, value="docx").pack(anchor="w", padx=180)
ttk.Radiobutton(root, text="PDF (.pdf)", variable=format_var, value="pdf", state=DISABLED).pack(anchor="w", padx=180)

Button(root, text="문서 생성하기", command=create_document,
       bg="#4CAF50", fg="white", font=("맑은 고딕", 14, "bold"), width=36, height=2).pack(pady=30)

root.mainloop()
